package com.krb.guaranty.common.business.sign;import org.springframework.util.DigestUtils;public class IFCSignUtil {    public static String sign(String bodyString,String key){        return getMD5String(String.valueOf(bodyString).concat(String.valueOf(key)));    }    public static String getMD5String(String str) {        return DigestUtils.md5DigestAsHex(str.getBytes());    }}